import React, {useState, useEffect} from 'react';
import styled from '@emotion/styled';
import socket from '../socket';

const darkGrey = '#303030';

const Wrapper = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 2;
`

const Input = styled.input`
border-radius: 5vw;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 50%;
padding: 1em 2em 1em 2em;
outline: none;
&:focus {
    color: ${darkGrey};
}`

const Slider = styled.input`
width: 25%;
`

const ChatInput = () => {
    const [inputText, setInputText] = useState('Say something...');
    const [pitch, setPitch] = useState(1);
    const [rate, setRate] = useState(1);
    const handleKeyPress = (e) => {
        if(e.key === 'Enter'){
          let data = {
              text: inputText,
              pitch: pitch,
              rate: rate
          }
          socket.emit('msg', data); // send server our message, the pitch, and the rate
          setInputText('');
        }
    }

    useEffect(() => {
        socket.on('msg', data => { // tell the client to speak a message with specified pitch and rate
            let utterance = new SpeechSynthesisUtterance(data.text);
            utterance.pitch = data.pitch;
            utterance.rate = data.rate;
            speechSynthesis.speak(utterance);
            console.log(data.text);
        });

        return () => {
            socket.off('msg');
        };
    });

    return (
    <>
        <Input autoComplete="off" type="text" name="name" onFocus={() => setInputText('')} onKeyPress={handleKeyPress} 
        onBlur={() => setInputText('Say something...')} value={inputText} onChange={e => setInputText(e.target.value)}/>
        <Slider type="range" min="0" max="2" id="myRange" value={pitch} onChange={e => setPitch(e.target.value)}/>
        <Slider type="range" min="0" max="3" id="myRange" value={rate} onChange={e => setRate(e.target.value)}/>
    </> 
    )
}

export default ChatInput;
