import React, {useState, useEffect} from 'react';
import Speaker from './Speaker';
import styled from '@emotion/styled';
import socket from '../adapters/socket';


const Wrapper = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 2;
`

const Wrapper2 = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 3;
`

const StyledInput = styled.input`
border-radius: 100em;
box-sizing: border-box;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 75%;
padding: 1.5em 2em 1.5em 2em;
outline: none;
&:focus {
    color: #303030;
}
@media (min-width: 1024px) {
   width: 50%;
}
`

const Slider = styled.input`
width: 25%;
`

const ChatInput = ({synth}) => {
    let utterance;
    let user;
    let data;
    // I thought it was an issue with Chrome but its most likely an issue with React
    let userName = '';
    const [inputText, setInputText] = useState('Say something...');
    const [pitch, setPitch] = useState(1);
    const [rate, setRate] = useState(1);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (userName === '') {
            userName = inputText;
            user = {id: socket.id, name: userName}
            socket.emit('newUser', user);
            console.log(user);
            utterance = new SpeechSynthesisUtterance(`${inputText} has joined the chat`);
            synth.speak(utterance);
        }

        else {
            data = {
            text: inputText,
            pitch: pitch,
            rate: rate
            }
            socket.emit('msg', data); // send server our message, the pitch, and the rate
            setInputText('');
        }
        
    }

    useEffect(() => {

            socket.on('msg', data => { // tell the client to speak a message with specified pitch and rate
                utterance = new SpeechSynthesisUtterance(data.text);
                synth.speak(utterance);
            });
            
            return () => {
                socket.off('msg');
            };
    });

    return (
    <>
    <Wrapper>
    <form onSubmit={handleSubmit}>
        <StyledInput maxLength="32" autoComplete="off"  type="text" name="name"
        value={inputText} onChange={(e) => setInputText(e.target.value)}>
        </StyledInput>
    </form>
    </Wrapper> 
    </>
    )
}

export default ChatInput;
