import React, {useState} from 'react'
import styled from '@emotion/styled';
import socket from '../adapters/socket';

const UserWrapper = styled.div`
display: flex;
flex-wrap: wrap;
flex-direction: row;
`

const Username = styled.div`
display:flex;
justify-content: center;
align-items; center;
width: 100%;
`

const [color, setColor] = useState('black');

const User = user => {
    if (user.id === socket.id) setColor('red');
    return(
        <UserWrapper>
            <Username style={{color: 'red'}}>{user.name}</Username>
        </UserWrapper>
    )
}

export default User;
