import React, {useState, useEffect} from 'react';
import styled from '@emotion/styled';
import socket from '../socket';

const Input = styled.input`
border-radius: 5vw;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 75%;
padding: 1em 2em 1em 2em;
outline: none;
`

const ChatInput = () => {
    return (
    <>
        <input ref={textInput} autoComplete="off" type="text" name="name" onFocus={() => textInput.current.value = ''} 
        onBlur={() => textInput.current.value = 'Say something...'} value={message} onChange={e => setMessage(e.target.value)}/>
    </> 
    )
}

export default ChatInput;
