import React, {useEffect, useState} from 'react'
import styled from '@emotion/styled';
import socket from '../socket';

const UserWrapper = styled.div`
display: flex;
flex-wrap: wrap;
flex-direction: row;
width: 5%;
`

const Avatar = styled.div`
display:flex;
justify-content:center;
border-radius: 100em;
width: 100%;
aspect-ratio: 1/1;
`

const Username = styled.div`
display:flex;
justify-content: center;
align-items; center;
width: 100%;
`

const User = user => {
    if (user.id === socket.id) user.color = 'red';
    return(
        <UserWrapper>
            <Avatar style={{background: user.color}}></Avatar>
            <Username style={{color: user.color}}>{user.name}</Username>
        </UserWrapper>
    )
}

export default User;
