import React, {useEffect, useState} from 'react'
import styled from '@emotion/styled';

const Avatar = styled.div`
display: flex;
padding: 1rem;
justify-content: center;
align-items: center;
width: 10%;
aspect-ratio: 1 / 1;
background: ${ Math.floor(Math.random()*16777215).toString(16) };
font-family: 'Monosten';
font-weight: 200;
`

const User = ({name}) => {
    return(
        <Avatar>{name}</Avatar>
    )
}

export default User;
