import React, {useState, useEffect} from 'react';
import styled from '@emotion/styled';

const Wrapper = styled.div`
position: fixed;
z-index:100;
width:100%;
height:100%;
display:flex;
background: #f2f3f5;
align-items: center;
justify-content: center;
`
const Red = styled.span`
    color:#FF331F;
`

const Box = styled.div`
display: grid;
grid-template-columns: repeat(1, 1fr);
grid-auto-rows: minmax(1vh, auto);
background: white;
box-shadow: 0 1px 3px rgb(0 0 0 / 12%), 0 1px 2px rgb(0 0 0 / 24%);
position: sticky;
border-radius: .5em;
width:75vw;
height:75vh;
padding: 2em;
`
const Big = styled.h2`
font-family: 'Monosten';
font-weight: 300;
margin: 0;
`

const BigWrapper = styled.div`
display:flex;
justify-content: center;
align-items: start;
height: 75vh;
`;

const InputWrapper = styled.div`
`;

const StyledInput = styled.input`
border-radius: 100em;
box-sizing: border-box;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 100%;
padding: 1.5em 2em 1.5em 2em;
outline: none;
&:focus {
    color: #303030;
}`

const Card = () => {

    return(
        <Wrapper>
            <Box>
                <BigWrapper><Big>Welcome to <Red>aloud</Red>, the text-to-speech chatroom.</Big></BigWrapper>
                <InputWrapper><StyledInput/></InputWrapper>
            </Box>
        </Wrapper>
    )
}

export default Card;
