import React, {useState, useEffect} from 'react';
import Speaker from './Speaker';
import styled from '@emotion/styled';
import socket from '../socket';

const Wrapper = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 2;
`

const Wrapper2 = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 3;
`

const StyledInput = styled.input`
border-radius: 100em;
box-sizing: border-box;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 75%;
padding: 1.5em 2em 1.5em 2em;
outline: none;
&:focus {
    color: #303030;
}
@media (min-width: 1024px) {
   width: 50%;
}
`

const Slider = styled.input`
width: 25%;
`

const ChatInput = () => {
    const [inputText, setInputText] = useState('Say something...');
    const [disabled, setDisabled] = useState(false);
    const [pitch, setPitch] = useState(1);
    const [rate, setRate] = useState(1);
    const handleKeyPress = (e) => {
        if(e.key === 'Enter'){
          let data = {
              text: inputText,
              pitch: pitch,
              rate: rate
          }
          socket.emit('msg', data); // send server our message, the pitch, and the rate
          setInputText('');
        }
    }

    return (
    <>
    <Wrapper>
        <Speaker />
        <StyledInput autoFocus="true" maxLength="32" disabled={disabled} autoComplete="off"  type="text" name="name" onFocus={() => {setInputText(''); }} onKeyPress={handleKeyPress} 
        onBlur={() => {setInputText('Say something...');}} value={inputText} onChange={(e) => setInputText(e.target.value)}>
        </StyledInput>
    </Wrapper> 
    </>
    )
}

export default ChatInput;
