import React, {useEffect, useState} from 'react'
import styled from '@emotion/styled';
import socket from '../socket';
let RandomColor = () => Math.floor(Math.random()*16777215).toString(16);

const colors = ['#FFDEAA', '#FFCAA3', '#FFD9AE', '#FFB59B', '#FFA194', '#FF8C8C'];

const UserWrapper = styled.div`
display: flex;
flex-wrap: wrap;
flex-direction: row;
width: 5%;
`

const Avatar = styled.div`
display:flex;
justify-content:center;
border-radius: 100em;
width: 100%;
aspect-ratio: 1/1;
`

const Username = styled.div`
display:flex;
justify-content: center;
align-items; center;
width: 100%;
`

const User = user => {
    if (user.id === socket.id)
    {
        user.color = red;
    }
    else user.color = colors[Math.floor(Math.random()*colors.length)];
    return(
        <UserWrapper>
            <Avatar style={{background: user.color}}></Avatar>
            <Username style={{}}>{user.name}</Username>
        </UserWrapper>
    )
}

export default User;
