import React, {useState} from 'react';
import styled from '@emotion/styled';

const darkGrey = '#303030';

const StyledInput = styled.input`
border-radius: 5vw;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 50%;
padding: 1em 2em 1em 2em;
outline: none;
&:focus {
    color: ${darkGrey};
}`

const Input = (props) => {
    const [inputText, setInputText] = useState(props.defaultText ? props.defaultText : '');
    return(
    <StyledInput autoComplete="off" type="text" onFocus={() => setInputText('')} 
    onBlur={() => setInputText(props.defaultText)} value={inputText} onChange={e => setInputText(e.target.value)}></StyledInput>
    )
}

export default Input;
