import React, {useEffect, useState} from 'react'
import socket from '../socket';
import styled from '@emotion/styled';


const ListWrapper = styled.div`
display: grid;
grid-template-columns: repeat(auto-fit, 5%);
grid-gap: 0;
  > div {
    background: lavender;
    padding: 1rem;
    display: flex;
    justify-content: center;
    align-items: center;
    &::before {
      // for apsect ratio
      content: "";
      display: block;
      padding-bottom: 100%;
      grid-area: 1 / 1 / 2 / 2;
    }
    img {
      width: 100%;
      // height: 100%;
      // object-fit: contain;
      grid-area: 1 / 1 / 2 / 2;
    }
  }
`

const UserList = ({users}) => {
    
    return (
        <ListWrapper>
            {users.map( user =>
          <div>{ user.name }</div>
            )}
        </ListWrapper>
    )
}

export default UserList;
