import React, {useEffect, useState} from 'react'
import styled from '@emotion/styled';

let RandomColor = () => Math.floor(Math.random()*16777215).toString(16);

const UserWrapper = styled.div`
display: flex;
flex-wrap: wrap;
flex-direction: row;
`

const Avatar = styled.div`
display:flex;
justify-content:center;
border-radius: 100em;
> div {
    display: flex;
    padding: 1rem;
    justify-content: center;
    align-items: center;
    width: 10%;
    aspect-ratio: 1 / 1;
    font-family: 'Monosten';
    font-weight: 200;
}
`

const Username = styled.div`
display:flex;
justify-content: center;
align-items; center;
`

const User = ({name}) => {
    return(
        <UserWrapper>
            <Avatar style={{background:'#' + RandomColor()}}><div></div></Avatar>
            <Username>{name}</Username>
        </UserWrapper>
    )
}

export default User;
