import React, {useState, useEffect} from 'react';
import Speaker from './Speaker';
import styled from '@emotion/styled';
import socket from '../adapters/socket';

let utterance;

const Wrapper = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 2;
`

const Wrapper2 = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 3;
`

const StyledInput = styled.input`
border-radius: 100em;
box-sizing: border-box;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 75%;
padding: 1.5em 2em 1.5em 2em;
outline: none;
&:focus {
    color: #303030;
}
@media (min-width: 1024px) {
   width: 50%;
}
`

const Slider = styled.input`
width: 25%;
`

const ChatInput = ({synth}) => {

    // I thought it was an issue with Chrome but its most likely an issue with React
    const [userName, setName] = useState('');
    const [inputText, setInputText] = useState('Say something...');
    const [pitch, setPitch] = useState(1);
    const [rate, setRate] = useState(1);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (userName === '') {
            let user = {id: socket.id, name: inputText}
            socket.emit('newUser', user);
            //let data = {text: `${userName} has joined the chat`, pitch: 1, rate: 1}
            //socket.emit('msg', data);
            setName(inputText);
            utterance = new SpeechSynthesisUtterance(`${textInput} has joined the chat`);
            synth.speak(utterance);
            console.log(user);
        }

        else {
            let data = {
            text: inputText,
            pitch: pitch,
            rate: rate
            }
            socket.emit('msg', data); // send server our message, the pitch, and the rate
            setInputText('');
        }
        
    }

    useEffect(() => {
        if(socket.connected){
            socket.on('tts', data => { // tell the client to speak a message with specified pitch and rate
                console.log(data.text);
                utterance = new SpeechSynthesisUtterance(data.text);
                synth.speak(utterance);
                  
            });
        }
            
            return () => {
                if (socket.connected)
                socket.off('tts');
            };
    });

    return (
    <>
    <Wrapper>
    <form onSubmit={handleSubmit}>
        <StyledInput autoFocus={true} maxLength="32" autoComplete="off"  type="text" name="name"
        value={inputText} onChange={(e) => setInputText(e.target.value)}>
        </StyledInput>
    </form>
    </Wrapper> 
    </>
    )
}

export default ChatInput;
