import React, {useState, useEffect} from 'react';
import styled from '@emotion/styled';
import socket from '../socket';

const darkGrey = '#303030';

const Input = styled.input`
border-radius: 5vw;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 75%;
padding: 1em 2em 1em 2em;
outline: none;
&:focus {
    color: ${darkGrey};
}
`

const ChatInput = () => {
    const textbox = React.useRef();
    const [message, setMessage] = useState('Say something...');
    const handleKeyPress = (e) => {
        if(e.key === 'Enter'){
          setMessage('');
        }
    }

    return (
    <>
        <Input ref={textbox} autoComplete="off" type="text" name="name" onFocus={() => setMessage('')} 
        onBlur={() => setMessage('Say something...')} value={message} onChange={e => setMessage(e.target.value)}/>
    </> 
    )
}

export default ChatInput;
