import React, {useState, useEffect} from 'react';
import styled from '@emotion/styled';
import socket from '../adapters/socket';

const Wrapper = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 2;
`

const StyledInput = styled.input`
border-radius: 100em;
box-sizing: border-box;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
width: 100%;
padding: 1em 2em 1em 2em;
outline: none;
&:focus {
    color: #303030;
}
@media (min-width: 1024px) {
   width: 50%;
}
`

const ChatInput = () => {
    useEffect(() => {
        socket.on('speak', text => {
                console.log(text);
                let utterance = new SpeechSynthesisUtterance(text);
                synth.speak(utterance);
          });

          return (() => {
              socket.off('speak');
          })

    });

    let user;
    let synth = window.speechSynthesis;
    const defaultText = 'Enter your name to join';
    // I thought it was an issue with Chrome but its most likely an issue with React
    const [userName, setName] = useState('');
    const [inputText, setInputText] = useState(defaultText);

    const handleSubmit = (e) => {
        e.preventDefault();
        e.target.blur();
        if (userName === '') {
            user = {id: socket.id, name: inputText}
            socket.emit('newUser', user);
            console.log(user);
            setName(inputText);
        }

        else {
            socket.emit('msg', inputText); // send server our message, the pitch, and the rate
            setInputText('');
        }
        
    }

    return (
    <>
    <Wrapper>
    <form style={{ width:'75%', display:'flex', justifyContent:'center' }} onSubmit={handleSubmit}>
        <StyledInput maxLength="32" autoComplete="off"  type="text" name="name" onBlur={(e) => {
        userName === '' ? setInputText(defaultText) : setInputText('Say something...');
      }} value={inputText} onChange={(e) => setInputText(e.target.value)}>
        </StyledInput>
    </form>
    </Wrapper> 
    </>
    )
}

export default ChatInput;
