import React, {useState, useEffect} from 'react';
import Speaker from './Speaker';
import styled from '@emotion/styled';
import socket from '../adapters/socket';

let utterance;

const Wrapper = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 2;
`

const Wrapper2 = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 3;
`

const StyledInput = styled.input`
border-radius: 100em;
box-sizing: border-box;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 2vh;
width: 75%;
padding: 1.5em 2em 1.5em 2em;
outline: none;
&:focus {
    color: #303030;
}
@media (min-width: 1024px) {
   width: 50%;
}
`

const Slider = styled.input`
width: 25%;
`

const ChatInput = ({synth}) => {

    // So in stupid stupid Chrome, speechSynthesis can not speak unless at some point the user activates it.
    // This means that clients can not speak unless a message has been sent to them by someone else first.
    
    const [inputText, setInputText] = useState('Say something...');
    const [pitch, setPitch] = useState(1);
    const [rate, setRate] = useState(1);
    const handleKeyPress = (e) => {
        if(e.key === 'Enter'){
          let data = {
              text: inputText,
              pitch: pitch,
              rate: rate
          }
          socket.emit('msg', data); // send server our message, the pitch, and the rate
          setInputText('');
        }
    }
    
    const handleSubmit = (e) => {
        e.preventDefault();
        let data = {
            text: inputText,
            pitch: pitch,
            rate: rate
        }
        socket.emit('msg', data); // send server our message, the pitch, and the rate
        setInputText('');
    }

    useEffect(() => {
            socket.on('tts', data => { // tell the client to speak a message with specified pitch and rate
                utterance = new SpeechSynthesisUtterance();
                console.log(utterance);   
                synth.speak(utterance);
        });
            return () => {
                socket.off('tts');
            };
    });

    return (
    <>
    <Wrapper>
    <form onSubmit={handleSubmit}>
        <StyledInput autoFocus={true} maxLength="32" autoComplete="off"  type="text" name="name" onFocus={() => {setInputText(''); }} 
        value={inputText} onChange={(e) => setInputText(e.target.value)}>
        </StyledInput>
    </form>
    </Wrapper> 
    </>
    )
}

export default ChatInput;
