import React, {useState, useEffect} from 'react';
import styled from '@emotion/styled';
import socket from '../adapters/socket';

const Wrapper = styled.div`
display:flex;
justify-content: center;
align-items: center;
height: 25vh;
grid-row: 2;
`

const StyledInput = styled.input`
border-radius: 100em;
box-sizing: border-box;
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
width: 75%;
padding: 1em 2em 1em 2em;
outline: none;
transition-duration: .3s;
&:focus {
    color: #303030;
}
&:hover {
    background-color: #eeeff2;
}

&:hover:not(:focus) {
    color: #a5a5a5;
}
@media (min-width: 1024px) {
   width: 50%;
}
`

const Chat = () => {
    // Set up variables and initial state
    let user;
    const defaultText = 'Enter your name to join';
    const [inputText, setInputText] = useState(defaultText);
    
    // On submit: 
    const handleSubmit = (e) => {
        e.preventDefault();
        e.target.blur();
        if (user === undefined) { // If we are not a user, become one and send our info to the server
            user = {id: socket.id, name: inputText}
            socket.emit('newUser', user);
        }

        else { // If we are a user, send the value of our input to the server and reset our input
            socket.emit('msg', inputText); 
            setInputText('');
        }
        
    }

    useEffect(() => {
        socket.on('speak', text => {
                console.log(text);
                let utterance = new SpeechSynthesisUtterance(text);
                let synth = window.speechSynthesis;
                synth.speak(utterance);
          });

          return (() => {
              socket.off('speak');
          })
    });


    return (
    <>
    <Wrapper>
    <form style={{ width:'100%', display:'flex', justifyContent:'center' }} onSubmit={handleSubmit}>
        <StyledInput maxLength="32" autoComplete="off"  type="text" name="name" onFocus={(e) => setInputText('')}
         onBlur={(e) => { user === undefined ? setInputText(defaultText) : setInputText('Say something...'); }}
          value={inputText} onChange={(e) => setInputText(e.target.value)}>
        </StyledInput>
    </form>
    </Wrapper> 
    </>
    )
}

export default Chat;
