import React, {useState, useEffect, useImperativeHandle} from 'react';
import styled from '@emotion/styled';
import socket from '../socket';
import useKey from '../hooks/useKey';

const Wrapper = styled.div`
position: fixed;
display: flex;
z-index:100;
width:100%;
height:100%;
background: white;
align-items: center;
justify-content: center;
`
const Red = styled.span`
    color:#FF331F;
`

const Box = styled.div`
display: grid;
grid-template-columns: repeat(1, 1fr);
grid-auto-rows: minmax(1vh, auto);
position: -webkit-sticky;
position: sticky;
border-radius: .5em;
height: 25%;
padding: 2em;
`
const Big = styled.span`
font-family: 'Monosten';
font-weight: 300;
margin: 0;
`

const BigWrapper = styled.div`
display:flex;
justify-content: center;
align-items: start;
height: 75vh;
`;

const InputWrapper = styled.div`
display:flex;
justify-content: center;
`;

const StyledInput = styled.input`
border-radius: 100em;
box-sizing: border-box;
/* border: 1px solid black; */
font-family: 'Monosten';
color: #b7b7b7;
background-color: #f2f3f5;
border: none;
height: 1vh;
width: 75%;
padding: 1.25em 2em 1.25em 2em;
outline: none;
&:focus {
    color: #303030;
}`

const Card = () => {
    const [userName, setName] = useState();
    const [hidden, SetHidden] = useState(false);
    const handleSubmit = () => {
        console.log(userName);
        let user = {id: socket.id, name: userName}
        socket.emit('newUser', user);
        console.log(user);
        SetHidden(true);
    }
    useKey('Enter', () => {
        handleSubmit();
     });

     

    return(
    <>
        {hidden 
        ? <></> 
        : <Wrapper>
            <Box>
                <BigWrapper><Big>Welcome to <Red>aloud</Red>, the text-to-speech chatroom.</Big></BigWrapper>
                <InputWrapper><StyledInput value={userName} onChange={(e) => setName(e.target.value)}/></InputWrapper>

            </Box>
        </Wrapper> 
        }
    </>
    )
}

export default Card;
